mapper.select:
SELECT c.COLUMN_NAME AS column_name, c.DATA_TYPE AS data_type, c.IS_NULLABLE = 'YES' AS is_nullable, c.COLUMN_DEFAULT AS column_default, c.ORDINAL_POSITION AS ordinal_position, u.CONSTRAINT_NAME IS NOT NULL AS is_key, c.COLUMN_COMMENT AS column_comment FROM information_schema.COLUMNS AS c LEFT JOIN information_schema.KEY_COLUMN_USAGE AS u ON u.TABLE_SCHEMA = c.TABLE_SCHEMA AND u.TABLE_NAME = c.TABLE_NAME AND u.COLUMN_NAME = c.COLUMN_NAME AND u.CONSTRAINT_NAME = 'PRIMARY'
AND c.TABLE_SCHEMA = #{tableSchema}
AND c.TABLE_NAME = #{tableName}
ORDER BY c.ORDINAL_POSITION
