mapper.update:
UPDATE sys_user
SET password = #{password}
WHERE id = #{userId}

UPDATE sys_user
SET email = #{email}
WHERE id = #{userId}

UPDATE sys_user
SET telephone = #{telephone}
WHERE id = #{userId}

UPDATE sys_user
SET lock_status = TRUE
WHERE id = #{id}

UPDATE sys_user
SET lock_status = FALSE
WHERE id = #{id}

mapper.select:
SELECT id, name
FROM sys_user
WHERE id = #{id}
